        <div className="cart">
            <div className="popup">
              <p>Shopping Cart ({this.props.itemCount} items)</p>
              {this.props.cart.map((roll) => console.log("added!", roll.name))}
              <p>HELLLOOOOOO</p>
                <span className="popuptext" id="myPopup">
                    <p>Added to cart: </p>
                    <b>{roll.name}</b>
                    <p>{roll.glaze} glazing</p>
                    <p>Pack of {roll.pack}</p>
                    <p>Price: ${roll.price}</p>
                </span>
            </div>

        </div>

    );
  }
}

export default Cart;
