import React, { Component } from 'react';
import './NavBar.css';
import logo from './logo/logo-01.svg';
import Cart from './Cart';
import './Roll.css';


class NavBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isCartExpanded: false
    };
    this.toggleCart = this.toggleCart.bind(this);
  }

  toggleCart() {
    this.setState({
      isCartExpanded: !this.state.isCartExpanded
    })
  }

  render() {
    console.log("this.state.isCartExpanded", this.state.isCartExpanded)
    console.log("state of cart: ", this.props.cart)
    let cartClass = 'cart';
    if (this.state.isCartExpanded) {
      cartClass += ' expanded';
    }

    return (
      <header>
        <img className="logo" src={logo} alt="logo of the bun bun bake shop which is a doodle of a cinammon roll"/>
        <div className="navbar">
          <div>
            <a>PRODUCTS </a>
            <button className="navbar-cart" onClick={this.toggleCart}>CART</button>
          </div>
          <div id="cart-status">
            <p>{this.props.itemCount} item</p>
            <p>Total: $ {this.props.totalPrice}</p>
          </div>
          <hr/>
          <p className="tagline">Our hand-made cinnamon rolls</p>
          <div className={cartClass}>
          <Cart
              itemCount= {this.props.itemCount}
              totalPrice= {this.state.cartTotalPrice.toFixed(2)}
              latestRoll= {this.state.currentRoll}
              cart= {this.state.cart}
            />
          </div>
          <div className="search">
            <input type="text"></input>
            <button type="submit">Search</button>
          </div>

        </div>

      </header>

    );
  }
}

export default NavBar;
