          <p>Shopping Cart ({this.props.itemCount} items)</p>
          <p>Total: $ {this.props.totalPrice}</p>
          <div>
            {this.props.cart.map((rollInCart) => 
              {
                return (
                    <div>
                      {/* {console.log("URL", {rollInCart})}
